<?phpnamespace App\Http\Middleware;use Closure;class User{    /**     * Handle an incoming request.     *     * @param $request     * @param Closure $next     * @param $action     * @return \Illuminate\Http\RedirectResponse|mixed     */    public function handle($request, Closure $next, $action){        $attribution = 'module_user_'.$action;        /*         * Empêcher l'utilisateur d'effectuer l'action s'il n'est pas autorisé.         */        if(auth()->user()->espace->{$attribution} != 1) {			session()->flash('type', 'alert-danger');			session()->flash('message', 'Vous n\'etes pas autorisé à accéder à cette section');            return redirect()->back();        }        return $next($request);    }}